con:config.script:
// Get current index
def currentIndex = testRunner.testCase.testSteps['Properties'].getPropertyValue("CurrentIndex") as int
// Get rows count
def rowsCount = testRunner.testCase.testSteps['Properties'].getPropertyValue("RowCount") as int

if (currentIndex < rowsCount){
	// Increase current index
	currentIndex = currentIndex + 1
	// Update currentIndex property
	testRunner.testCase.testSteps['Properties'].setPropertyValue("CurrentIndex", currentIndex as String)

	// Loop
	testRunner.gotoStepByName("Property Transfer")
}
else{
	// We are ready and we will set currentIndex and rowCount to initial state
	// Get current index
	testRunner.testCase.testSteps['Properties'].setPropertyValue("CurrentIndex", "1")
	testRunner.testCase.testSteps['Properties'].setPropertyValue("RowCount", "1")
}
